import React, { Component } from "react";
import { getMovies } from "../services/fakeMovieService";

class Movies extends Component {
  constructor(props) {
    super(props);
    this.state = {
      movies: getMovies(),
      countMoviesFraze: (
        <span>
          Showing <strong>{getMovies().length}</strong> movies in database
        </span>
      ),
    };
  }

  deleteButton = (movie_id) => {
    let newCountMoviesFrazes =
      this.state.movies.length > 1 ? (
        <span>
          Showing <strong>{this.state.movies.length - 1}</strong> movies in
          database
        </span>
      ) : (
        "There are no movies in the data base"
      );
    this.setState({
      movies: this.state.movies.filter((movie) => movie._id !== movie_id),
      countMoviesFraze: newCountMoviesFrazes,
    });
  };

  render() {
    return (
      <div className="App">
        <span>{this.state.countMoviesFraze}</span>
        <table className="table">
          <thead>
            <tr>
              <th scope="col">Title</th>
              <th scope="col">Genre</th>
              <th scope="col">Stork</th>
              <th scope="col">Rate</th>
              <th scope="col"></th>
            </tr>
          </thead>
          <tbody>
            {this.state.movies.map((element) => (
              <tr key={element._id}>
                <td>{element.title}</td>
                <td>{element.genre.name}</td>
                <td>{element.numberInStock}</td>
                <td>{element.dailyRentalRate}</td>
                <td>
                  <button
                    className="btn btn-danger"
                    onClick={() => this.deleteButton(element._id)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
            ;
          </tbody>
        </table>
      </div>
    );
  }
}

export default Movies;
