import React, { Component } from "react";

class Like extends Component {
  constructor(props) {
    super(props);
    this.state = {
      liked: false,
    };
  }
  render() {
    //let forLikeClass = this.state.liked ? "r" : "s";
    return (
      <div>
        <i className={"fa" + this.state.liked ? "r" : "s" + " fa-heart"}></i>
        <i className="far fa-heart"></i>
      </div>
    );
  }
}

export default Like;
