import React, { Component } from "react";

class Like extends Component {
  constructor(props) {
    super(props);
    this.state = {
      liked: this.props.liked,
    };
  }

  componentDidUpdate(prevProps) {
    if (this.props.liked !== prevProps.liked)
      this.setState({
        liked: this.props.liked,
      });
  }

  render() {
    console.log("Like Rendered");
    return (
      <div>
        <i
          className={"fa fa-heart" + (this.state.liked ? "" : "-o")}
          aria-hidden="true"
          onClick={() => this.props.onLiked(this.props.id)}
        ></i>
      </div>
    );
  }
}

export default Like;
